INSERT INTO `products` VALUES ('66', '1', '1', '66', 'Vodka Absolut Pears', '', 'Vodka Absolut Pears é uma novidade da família Absolut, introduzida em 2007. É feita exclusivamente de ingredientes naturais, e diferentemente da maior parte de outras vodkas com sabor, esta não contém açúcar acrescentado. A Absolut Pears é fresca e delicada, com um gosto de pêras maduras e um sabor frutado que se demora. ', '', '', '', '', null, '', '49', '97.57', '139.50', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('67', '1', '1', '67', 'Vodka Absolut 100 – Special Serie', '', 'Vodka Absolut 100 – Special SerieSérie especial para colecionadores ou apreciadores de uma otima vodka. Absolut 100 é uma marca nova, com sabor mais encorpado, direcionada para o público masculino. Esta tem o sabor mais pronunciado, aflorando notas cítricas e herbáceas conforme o distinto paladar da vodka vai se dissipando.O estilo rústico, qualidades masculinas da Absolut 100 são enfatizadas no duro conceito de design e de imagem, com destaque para as marcantes cores que foram utilizadas, preto opaco e prata.', '', '', '', '', null, '', '0', '79.19', '113.20', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
