INSERT INTO `products` VALUES ('102', '1', '1', '102', 'Queijo Gorgonzola Quatá', '', 'O Gorgonzola originou-se \r\npor volta do ano de 900 DC na região dos vales formados pelo Rio Pó na \r\nItália. É um dos queijos mais importantes naquele país ao lado da \r\nmussarela, do provolone e do parmesão.  É um queijo de massa crua, que \r\napresenta textura quebradiça, relativamente mole, possuindo formações \r\ninternas de mofo azul originado pelo \" Penicillium Roqueforti\".  É \r\nexcelente como componente nos pratos culinários, molhos e patês. Mas \r\npode ser degustado puro acompanhado de vinho tinto e encorpado, como \r\nmolho de saladas é imperdível.', '', '', '', '', null, '', '267', '35.20', '50.30', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
