INSERT INTO `products` VALUES ('123', '1', '1', '123', 'Queijo Tipo Grana - Gran Formaggio', '', 'Gran Formaggioé o primeiro queijo tipo Grana produzido na América Latina. Segue toda a cultura clássica italiana de fabricação. Para atingir o ponto ideal de granulação e o sabor requintado que lhe é peculiar, o Gran Formaggio requer desde o leite de altíssima qualidade, vindo de rebanho próprio de vacas holandesas com cuidados especiais até um longo período de maturação. São no mínimo, 12 meses, após um trabalho artesanal, que une antigos rituais à mais avançada tecnologia do setor.', '', '', '', '', null, '', '180', '72.60', '103.80', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
