INSERT INTO `products` VALUES ('125', '1', '1', '125', 'Whisky Cutty Sark 8 Anos (1 L)', '', 'Whisky produzido e engarrafado na Escócia. Os maltes \r\nutilizados em sua fabricação são originários de Speyside, a região do \r\nchampagne para o Scotch Whisky. Os maltes são envelhecidos em barris de \r\ncarvalho americano, anteriormente utilizados para abrigar os melhores \r\nvinhos de Jerez. Cutty Sark foi o primeiro whisky de cor natural mantido\r\n até hoje.', '', '', '', '', null, '', '8', '77.00', '110.10', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
