INSERT INTO `products` VALUES ('140', '1', '1', '140', 'Vodka Absolut (500 ml)', '', 'Vodka Absolut provém das destilarias de Ahus, no sul da Suécia. Trigo de inverno selecionado, água da própria fonte e destilação contínua, um processo que remove todas as impurezas da vodka, mantendo o caráter suave e notável pureza, por esta razão a Absolut é uma vodka premium de grande qualidade.', '', '', '', '', null, '', '12', '47.44', '67.80', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
