INSERT INTO `products` VALUES ('155', '1', '1', '155', 'Pirarucu Filé c/ pele', '', 'O \r\nPirarucu é um dos maiores peixes de água doce fluviais e lacustres do \r\nBrasil e um dos maiores do mundo, conhecido também como o bacalhau da \r\nAmazônia.Restrito à Bacia Amazônica, Tocantins e Araguaia, nos \r\nterritórios de Mato Grosso e Goiás, pode atingir dois metros e meio e \r\nseu peso pode ir até 80 Kg. É o peixe de maior valor econômico e \r\nalimentício existente na Amazônia.', '', '', '', '', null, '', '96', '69.69', '99.60', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
