INSERT INTO `products` VALUES ('158', '1', '1', '158', 'Aspargo Fresco (maço)', '', '<p style=\"text-align: justify; \">\r\n<span style=\"font-size: 14px; font-family: Arial; color: rgb(33, 33, 33);\">Aspargo é um ingrediente rico em nutrientes, que possui baixo valor calórico e proporciona uma série de benefícios para o corpo e para a prevenção de uma série de doenças. Os aspargos destacam-se pela abundância de vitaminas A, B e C, e também pela presença de cálcio e ferro.Harmonização: Ideal para acompanhar vários pratos.</span></p><p><span style=\"font-size: 12px; font-family: Arial; color: rgb(33, 33, 33);\"><span style=\"text-decoration: underline; font-weight: bold; font-size: 14px;\"> Sugestão</span><span style=\"font-size: 14px;\">: acompanhando uma Paleta de Cordeiro ao forno.</span></span></p>', '', '', '', '', null, '', '75', '16.17', '23.10', null, '250', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
