INSERT INTO `products` VALUES ('171', '1', '1', '171', 'Picanha Nacional Bassi (Aproximadamente 1,200kg)', '', 'Picanha Nacional Dentre as carnes vermelhas, a picanha destaca-se por seu alto valor nutritivo, sendo rica na fonte de proteínas. A Picanha é originalmente um tipo de corte de carne bovina. Recentemente, o aumento da variedade de cortes de carnes suínas originado de ações promovidas pelos suinucultores, fez surgir um novo corte para os admiradores deste tipo de carne, que recebeu o nome de picanha suína.Apesar de controversa, uma das origens do termo parece ter saido do restaurante \"Esplanada Grill\" em São Paulo', '', '', '', '', null, '', '19', '113.52', '162.30', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
