INSERT INTO `products` VALUES ('193', '1', '1', '193', 'Abacate Avocado', '', 'É uma variedade do abacate, originária da Califórnia, que concentra grande quantidade de vitaminas, minerais, proteínas e fibras, sendo rico em 14 minerais e 11 vitaminas. Combate os radicais livres. Sua gordura é monoinsaturada, como o Azeite de Oliva. Seu consumo equilibra o Colesterol Rico em Fíbras. Rico em Vitaminas antioxidantes. Previne a cãibra porque é rico em potássio. Sacia a vontade de comer doces, por causa das fibras. Faz bem para o Coração e diminui a Impotência. Combate o envelhecimento.', '', '', '', '', null, '', '99', '29.90', '42.75', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
