INSERT INTO `products` VALUES ('224', '1', '1', '224', 'Batata Lavada', '', '<span style=\"font-family: Arial;\"><span style=\"color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">A&nbsp;</span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">&nbsp;é um vegetal que é da família Solanum. As batatas são consumidas em todas as parte do mundo e mantém um papel importante na culinária do dia a dia. As</span><span style=\"font-size: 15px;\">&nbsp;</span></span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">s podem ser encontradas em quase todas as refeições. Em fast foods como</span><span style=\"font-size: 15px;\">&nbsp;</span></span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">s fritas e</span><span style=\"font-size: 15px;\">&nbsp;</span></span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">s fritas francesas, que são um sucesso entre todas as idades. Também é utilizado em vários molhos e vegetais. As</span><span style=\"font-size: 15px;\">&nbsp;</span></span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">s podem ser cozidas, assadas ou preparadas para purê. A</span><span style=\"font-size: 15px;\">&nbsp;</span></span><span style=\"box-sizing: border-box; -webkit-font-smoothing: antialiased; outline: 0px; text-rendering: optimizeLegibility; vertical-align: baseline; color: rgb(119, 119, 119); font-size: 15px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\">Batata</span><span style=\"color: rgb(119, 119, 119); font-size: 17.008px; line-height: 28.9136px; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 15px;\">&nbsp;é rica em amido e é utilizada como um agente espessante em molhos e sopas.</span></span></span>', '', '', '', '', null, '', '84', '6.96', '9.99', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
