INSERT INTO `products` VALUES ('244', '1', '1', '244', 'Carne de Cordeiro (2,200 kl gr 20% +ou- )', '', 'A carne de cordeiro é macia, suculenta, de sabor pronunciado, não forte, ligeiramente, perfumada; Quando é acrescida de sal e pimenta em pequena quantidade, aumenta a salivação, sente-se muito mais o sabor e dá mais vontade de mastigar; Quando são outros temperos, além do sal e pimenta, sente-se o sabor da carne, mas o sabor do tempero fica mais pronunciado e já vem a vontade de ingerir um líquido ou comer uma salada.', '', '', '', '', null, '', '98', '78.06', '111.60', null, '2900', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
