INSERT INTO `products` VALUES ('273', '1', '1', '273', 'Faisão (Aproximadamente 1,700kg podendo variar seu peso em até 20% para mais ou para menos)', '', 'Carne de sabor marcante, o faisão tem uma textura diferente das aves comuns, o que a torna especial. \r\n\r\nComo a carne de faisão é rija, ela demora mais tempo até ficar tenra. Uma dica é retirar completamente os tendões, pois eles são extremamente duros.', '', '', '', '', null, '', '21', '184.80', '264.29', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
