INSERT INTO `products` VALUES ('390', '1', '1', '390', 'Vodka Absolut Mango', '', 'Absolut Mangoé preparada com frutas maduras e ingredientes 100% naturais, sem adição de açúcar ou adoçante. A nova bebida é apresentada na tradicional garrafa transparente, com design artístico assimétrico. A manga é explorada através de contornos em tons de amarelo, verde e vermelho alaranjado, visto que ela apresenta diferentes tonalidades de cor, dependendo de seu estágio de amadurecimento. O cuidado na preparação do produto, feito apenas com trigo de inverno devido à sua menor concentração de amido, garante uma bebida de alta qualidade comercializada em 126 mercados mundiais.', '', '', '', '', null, '', '50', '106.28', '151.99', null, '2000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
