INSERT INTO `products` VALUES ('415', '1', '1', '415', 'Cacau em Pó (500 gr)', '', '<p><span style=\"color: rgb(33, 33, 33);\"><span style=\"font-size: 15px; line-height: 22.5px; font-family: Arial; background-color: rgb(255, 255, 255);\">O cacau em pó é obtido através do processamento dos grãos do cacau. A maior parte de sua gordura é removida para outros preparos, restando uma pasta seca, que é liofilizada, se transformando em pó.&nbsp;</span><span style=\"font-family: Arial; font-size: 15px; line-height: 18px;\">Nutritivo, o cacau em pó é rico em proteínas e em cálcio e fonte de ferro. Fica ótimo em bolos, pudins, mousses, brigadeiros, no leite, ou como você inventar.</span></span></p>', '', '', '', '', null, '', '348', '17.42', '24.90', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
