INSERT INTO `products` VALUES ('424', '1', '1', '424', 'Arroz Negro LA PASTINA (1 kg)', '', '<p><span style=\"font-family: Arial; color: rgb(33, 33, 33);\">\r\n<span style=\"font-size: 15px;\"><span style=\"background-color: rgb(255, 255, 255);\">Cultivado na China\r\n há mais de 4 mil anos, com fama de produto afrodisíaco era chamado de \r\n“Arroz Proibido”, pois era consumido apenas pelo Imperador, cabendo a \r\nseus súditos somente a produção dos grãos</span>.</span><span style=\"font-size: 15px; line-height: 16.25px; text-align: justify; background-color: rgb(255, 255, 255);\">Ideal para o preparo de risotos e saladas ou como substituto do arroz branco comum ou do arroz integral.&nbsp;</span><span style=\"font-size: 15px; line-height: 16.25px; text-align: justify; background-color: rgb(255, 255, 255);\">Sob a forma de papa, tem importante papel no início da alimentação infantil, de convalescentes ou debilitados, além de beneficiar o funcionamento do coração e dos rins.</span></span></p>', '', '', '', '', null, '', '99', '38.50', '55.09', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
