INSERT INTO `products` VALUES ('426', '1', '1', '426', 'Queijo Pecorino Romano (1kg)', '', 'Queijo Pecorino romano Da região da Sardenha, é \r\nfeito com leite cru de ovelha. Menor que o romano, tem  forma cilíndrica\r\n e pesa de 1 kg a 4 kg. A casca é dura e lisa e sua cor varia  com a \r\nidade, começando pelo palha até o castanho-avermelhado-escuro. A textura\r\n é  granulosa e o sabor é de nozes e ervas. Há dois tipos de pecorino \r\nsardo, o doce  (maturado de 20 a 60 dias, pesa de 1 kg a 2 kg e tem a \r\nmassa branca e firme),  com sabor delicado e não picante, e o maduro \r\n(maturado por até 1 ano), que é  mais duro, seco e com textura bem \r\ngranulosa. Seu sabor é salgado e picante.', '', '', '', '', null, '', '186', '115.50', '165.19', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
