INSERT INTO `products` VALUES ('442', '1', '1', '442', 'Amêndoa Confeitada Colorida ISTAMBUL (pacote 200 g)', '', '<span style=\"color: rgb(98, 98, 98); font-family: Arial, &quot;Helvetica Neue&quot;, Helvetica, sans-serif; font-size: 16px; line-height: 22px; background-color: rgb(255, 255, 255);\">As amêndoas são muito comuns no casamento árabe e italiano. No Brasil, o costume é oferecer no final da cerimônia, seja no lugar dos bem-casados, em sopeiras na mesa dos doces ou dentro de uma caixinha decorada, como lembrancinha. O significado não muda: as amêndoas simbolizam a sorte e felicidade do casal. Muitos noivos também enviam amêndoas no convite de casamento ou junto com o cartão de agradecimento.A tradição das amêndoas surgiu na Itália e em Portugal, onde é comum familiares e convidados comerem amêndoas para trazer sorte aos noivos cada uma das cinco - diz a tradição que devem ser sempre cinco, tem um significado: saúde, riqueza, vida longa, fecundidade e felicidade.Ótima fonte de minerais, proteínas, vitamina E, potássio e cálcio, as amêndoas ajudam a manter o coração saudável.</span>', '', '', '', '', null, '', '19', '19.16', '27.40', null, '200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('443', '1', '1', '443', 'Soja em Grão', '', 'Considerada um grão sagrado, a soja faz parte da alimentação chinesa há cerca de cinco mil anos. Até os dias de hoje, a soja é tida no Oriente como um alimento básico.', '', '', '', '', null, '', '198', '7.84', '11.20', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
