INSERT INTO `products` VALUES ('449', '1', '1', '449', 'Água de Flor de Laranjeira ZEENNY (garrafa 260 ml)', '', 'Na culinária, as mais conhecidas são a água de flor de laranjeira - essência extraída das pétalas da flor de laranjeira (mazahr em árabe), e a água de rosas - solução alcoólica de essência de rosas, muito diluída com água destilada (maward), ambas utilizadas para aromatizar sorvetes, massas, cremes, caldas e pratos à base de carne de aves, entre outras iguarias, sobretudo na cozinha árabe, libanesa e indiana.', '', '', '', '', null, '', '19', '20.90', '29.89', null, '500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
