INSERT INTO `products` VALUES ('462', '1', '1', '462', 'Azeite COLAVITA Extra Virgem (1 L)', '', '<p style=\"vertical-align: baseline;\"><span style=\"font-family: Arial; color: rgb(33, 33, 33);\"><span style=\"orphans: 3; widows: 3; font-size: 15px;\">&nbsp;</span><span style=\"orphans: 3; widows: 3; font-size: 15px;\">Produzido com azeitonas colhidas nas três principais regiões produtoras da Itália (Puglia, Molise e Sicília) e prensado com azeitonas 50% maduras e 50% verdes. Este azeite tem sabor único, fresco e levemente picante,&nbsp;típico dos azeites italianos.</span><span style=\"line-height: 24px; orphans: 3; widows: 3; vertical-align: baseline; font-size: 15px;\">Harmonização:&nbsp;</span><span style=\"line-height: 24px; orphans: 3; widows: 3; font-size: 15px;\">Ideal para pratos frios. País: Itália.</span></span><span style=\"color: rgb(33, 33, 33); font-family: Arial; font-size: 15px;\">&nbsp;</span><span style=\"color: rgb(33, 33, 33); font-family: Arial; orphans: 3; widows: 3; font-size: 15px;\">Acidez Máxima: 0,6%.</span></p>', '', '', '', '', null, '', '84', '60.50', '86.50', null, '2', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
