INSERT INTO `products` VALUES ('469', '1', '1', '469', 'Vinho Italiano Branco Pinot Grigio IGT Vêneto SACCHETTO', '', 'Produtor: Sacchetto\r\nPaís: Itália\r\nRegião: Veneto\r\nSafra: 2006\r\nTipo: Branco Seco \r\nVolume: 750 ml\r\nUva: Pinot Grigio 100%\r\nVinhedos: Vinhedos localizados na região delimitada do Veneto.\r\nVinificação: Prensagem delicada. Leveduras selecionadas. Fermentação em \r\ncuba de aço inoxidável com controle de temperatura.\r\nMaturação: Naão passa por barricas de carvalho para guardar o máximo de \r\nfrescor.\r\n\r\nTemperatura de Serviço: 9 a 11ºC\r\n\r\nTeor Alcoólico: 12,5% Vol.\r\nCorpo: Leve\r\nSugestão de Guarda: Pronto para consumo\r\nCombinações: Peixes e frutos do mar', '', '', '', '', null, '', '0', '79.19', '113.20', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('470', '1', '1', '470', 'Vinho Chileno La Palma Cabernet Sauvignon/ Merlot 2006', '', 'Origem: Chile.Descrição: De cor vermelho profundo com tonalidades púrpura, Com sabores de cerejas, ameixas e algumas especiarias e um toque de carvalho.Harmonização: peixes, grelhados, saladas e carnes', '', '', '', '', null, '', '0', '79.19', '113.20', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
