INSERT INTO `products` VALUES ('542', '1', '1', '542', 'Ervilha - Kilo', '', '<i style=\"color: rgb(37, 37, 37); font-family: sans-serif; line-height: 22.4px; background-color: rgb(255, 255, 255);\"><b>Pisum sativum</b></i><span style=\"color: rgb(37, 37, 37); font-family: sans-serif; line-height: 22.4px; background-color: rgb(255, 255, 255);\">&nbsp;popularmente chamada de&nbsp;</span><b style=\"color: rgb(37, 37, 37); font-family: sans-serif; line-height: 22.4px; background-color: rgb(255, 255, 255);\">ervilha</b><span style=\"color: rgb(37, 37, 37); font-family: sans-serif; line-height: 22.4px; background-color: rgb(255, 255, 255);\">&nbsp;é uma planta (legume) da qual existem mais de duzentas variedades, e de suas vagens são extraídos diversos tipos de grãos. Em algumas regiões de alguns países é costume utilizar os grãos e as vagens como alimento, quer na forma de sopa (grãos ou vagem) ou como salada (grãos ou vagem). O seu consumo contribui para uma dieta equilibrada pois constituem uma fonte de fibras.</span>', '', '', '', '', null, '', '193', '9.90', '14.19', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('543', '1', '1', '543', 'Feijão Branco', '', 'Existem vários tipos de feijão, sendo que os mais comuns no Brasil são: carioquinha, preto, de corda, jalo, branco, rosado, fradinho, rajado e bolinha.É um alimento rico em proteínas vegetais.Possui também um alto valor calórico, sendo que 100 gramas de feijão apresenta 330 calorias.No Brasil, é muito consumido em combinação com o arroz e também para fazer a feijoada.Possui uma boa quantidade de fibras (cada 100 gramas de feijão apresenta, em média, 19 gramas de fibras).Ele é produzido durante todos os meses do ano.Além do ferro, possui os seguintes sais minerais (potássio, fósforo e cálcio).', '', '', '', '', null, '', '198', '34.84', '49.80', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('544', '1', '1', '544', 'Vinho Argentino Alamos Chardonnay Branco 2011', '', 'Origem: ArgentinoVinhedos: Vinhedos próprios de altitudes elevadas, Domingo e La \r\nPirámide, todos localizados na província de Mendoza. Vinificação: \r\nSeleção de leveduras. As uvas são fermentadas a temperatura  controlada,\r\n no máximo 20ºC, por 45 dias. Fermentação Malolática  parcialmente \r\nrealizada (70%), para conferir um toque cremoso sem perder o  frescor do\r\n vinho.', '', '', '', '', null, '', '0', '54.88', '78.49', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('545', '1', '1', '545', 'Vinho Argentino Chardonnay ANGELICA ZAPATA (750 ml)', '', 'Angelica Zapata é o melhor Chardonnay de Catena produzido para o mercado\r\n interno argentino, onde é considerado o melhor branco do país. \r\nIncrivelmente concentrado e potente, possui uma textura redonda e macia,\r\n com um leve toque amanteigado. As uvas são do magnífico vinhedo \r\nAdrianna, localizado a 940m de altitude, resultando em uma grande \r\namplitude térmica e um perfeito desenvolvimento das uvas.', '', '', '', '', null, '', '11', '214.50', '306.70', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
