INSERT INTO `products` VALUES ('547', '1', '1', '547', 'Licor Nocello Toschi (750ml)', '', 'Licor italiano elaborado à base de nozes, resultando em um sabor \r\ndelicioso e especial. Produzido pela empresa italiana Toschi. Na Idade \r\nMédia, era extraído das nozes um precioso elixir: o Nocello, licor que \r\nadornou mesas de reis e príncipes. Inspirada nessa lenda, a Toschi criou\r\n um licor de nozes capaz de captar com absoluta fidelidade toda a magia e\r\n sabor de uma bebida incomparável. O Nocello é resultado de um atento \r\ncontrole de qualidade e know-how em todos os níveis de produção, além de\r\n uma rigorosa escolha ds matérias-primas.', '', '', '', '', null, '', '2', '92.26', '131.90', null, '1400', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
