INSERT INTO `products` VALUES ('550', '1', '1', '550', 'Champagne DOM PERIGNON Black Box', '', 'Para os pais modernos, mas que não \r\nabrem mão da exclusividade, uma ótima opção é a quarta colaboração para o\r\n champagne Dom Pérignon do design Marc Newson, que projetou algo que \r\natende às necessidades de seu próprio estilo de vida, tirando a garrafa \r\nde seu pedestal apenas o suficiente para integrá-la na vida cotidiana e \r\ntorná-la ainda mais acessível. O resultado é um surpreendente estojo de \r\nviagem criado especialmente para uma garrafa de Dom Pérignon Vintage \r\n2000, feito de policarbonato, um material durável e moderno, que tem a \r\nvantagem da função térmica mantendo a bebida gelada por até duas horas. A\r\n garrafa é completamente isolada graças a um molde duplo: Black Box \r\nprotege o seu precioso conteúdo de choques e variações de temperatura, \r\nassim preservando-o, enquanto faz uma declaração de design toda própria,\r\n criada para viver em um ambiente de consumo', '', '', '', '', null, '', '0', '79.19', '113.20', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('551', '1', '1', '551', 'Azeite TAMEGÃO (500 ml)', '', '<p style=\"text-align: left; color: rgb(119, 119, 119); padding-left: 14.9688px; font-size: 17px; line-height: normal;\"><span style=\"font-size: 15px; font-family: Arial;\">Marca registada desde 1984. Este azeite era muito apreciado pela população da região do Douro, mais concretamente na região de Entre os Rios, onde deságua o rio Tamega, daí a origem da Marca Tamegão. O rio Tamega que é um rio internacional, que nasce na Serra de San Mamede, província de Ourense, (Galiza, Espanha) e desagua no rio Douro. O azeite Tamegão é uma combinação equilibrada entre saber e aroma, que permite a confecção dos mais variados pratos.</span></p>', '', '', '', '', null, '', '154', '28.00', '55.09', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
