INSERT INTO `products` VALUES ('555', '1', '1', '555', 'Bacalhau do Porto Filé Gadus Morhua (1,6 Kg)', '', 'Bacalhau do Porto Filé Bacalhau vindo da Noruega, de\r\nótima qualidade, apresenta grossas lascas, de bela cor e suave textura. Obs:\r\nSem pele e sem espinha.\r\n\r\nCaracterísticas:	Postas altas e claras. Desfaz-se em camadas claras e tenras quando cozido.\r\nSabor inconfundível e sublime.\r\n\r\nPratos sugeridos:     	Ideal para pratos onde o bacalhau é servido em postas, lombo ou pedaços. Preparações onde o bacalhau é o prato principal.\r\n\r\nColoração:	Palha e uniforme.\r\n\r\nCauda:	Triângulo, com coloração cinza e uniforme.', '', '', '', '', null, '', '953', '106.70', '152.59', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
