INSERT INTO `products` VALUES ('577', '1', '1', '577', 'Azeite Português VILA FLOR (500 ml)', '', 'Origem: Português Azeite Português Vila Flor, saboroso e com perfeita qualidade. Ideal para preparar vários tipos de pratos diferentes.', '', '', '', '', null, '', '0', '12.42', '17.79', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
