INSERT INTO `products` VALUES ('628', '1', '1', '628', 'Sálvia (mç)', '', 'Sálvia é um pequeno arbusto ou subarbusto, altura em torno de 50 centímetros. Acredita-se ser nativa da região do Mediterrâneo, mas atualmente é cultivada em várias regiões do mundo, mais para fins culinários do que medicinais. Suas folhas são verdes, oblongas(comprimento maior que largura), feitio semelhante ao de uma lança(lanceolada), coberta por pequenos pelos. Suas flores são violetas e pequenas. A Sálvia possui um odor forte e perfumado, por isso, é muito utilizado na culinária.', '', '', '', '', null, '', '94', '6.58', '9.40', null, '200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('629', '1', '1', '629', 'Tempero Sírio ', '', 'Tempero Sírio Este tempero é muito usado em receitas de quibes, kaftas e em vários pratos de carne e vegetais sírio-libaneses e do orinte médio. ', '', '', '', '', null, '', '299', '65.76', '94.00', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
