INSERT INTO `products` VALUES ('643', '1', '1', '643', 'Rúcula (maço)', '', 'A rúcula é uma hortaliça herbácea, folhosa, anual, porte baixo, com 15 a 20 centímetros de altura. A folha é a parte comestível e comercial da planta. A sua cor é verde-clara a verde-escura, forma alongada, profundamente recortada, tenra, sabor picante e, em condições de alta temperatura, ficam rijas, menores, picantes e amargas.', '', '', '', '', null, '', '81', '3.29', '4.70', null, '300', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
