INSERT INTO `products` VALUES ('655', '1', '1', '655', 'Vinho Português Tinto FILIPA PATO Ensaios', '', 'Tipo de Vinho: Tinto\r\nEnvelhecimento: Barrica de Madeira\r\nRegião Produtora: Bairrada e Dão\r\n\r\n\r\nVolume: 750ml\r\nSafra: 2006\r\nUva: Touriga Nacional 30%, Jaen 35% e Baga 35%\r\nGraduação Alcoólica: 13%\r\n\r\n\r\nViticultura/Vinificação: Provenientes de três castas portuguesas com \r\norigem em duas sub-regiões das Beiras. Touriga Nacional e Jaen de vinha \r\nde Vila Nova de Tazem e Baga de vinhas velhas da Bairrada. Vinhedo: \r\nTouriga Nacional e Jaen de solo granítico e Baga de argilo-calcário. \r\nVinificação: colheita manual. Fermentação com temperatura controlada \r\nabaixo dos 28ºC com extracção suave dos taninos com pisador pneumático. \r\nAfinamento: 9 meses em pipas de 650 ltrs de carvalho Allier, novo e \r\nusado, durante um período de 6 meses. Capacidade de envelhecimento de 8 \r\nanos.', '', '', '', '', null, '', '0', '35.91', '51.39', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
