INSERT INTO `products` VALUES ('669', '1', '1', '669', 'Uva Rosada', '', 'A uva é o fruto da videira, da família das\r\nVitaceae. É utilizada frequentemente para produzir sumo, doce (geleia), vinho e\r\npassas, podendo também ser consumida crua. Entre as espécies de uvas podemos\r\nreferir: Vitis vinifera, o tipo de uva mais frequente na produção do vinho , na\r\nEuropa; Vitis labrusca, espécie norte-americana, utilizada na produção de sumo,\r\nuva de mesa e, algumas vezes, vinho; Vitis riparia, tipo de uva bravio\r\nnorte-americano, usado, às vezes, para produzir vinho; Vitis rotundifolia, uva\r\nmuscadínea, usada para doces e, por vezes, vinho; Vitis aestivalis, em que a\r\nvariedade Norton é usada para fabricar vinho.', '', '', '', '', null, '', '163', '12.98', '18.59', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('670', '1', '1', '670', 'Presunto Crú Ibérico Pata Negra Fatiado Segovia (Kilo)', '', '<p style=\"margin-top: 0.5em; margin-bottom: 0.5em; line-height: 22.4px; color: rgb(37, 37, 37); font-family: sans-serif; background-color: rgb(255, 255, 255);\">O&nbsp;<b>presunto ibérico</b>&nbsp;ou&nbsp;<b>presunto de pata negra</b>, é um tipo de&nbsp;<a href=\"https://pt.wikipedia.org/wiki/Presunto\" title=\"Presunto\" style=\"color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">presunto</a>&nbsp;<a href=\"https://pt.wikipedia.org/wiki/Cura_(culin%C3%A1ria)\" title=\"Cura (culinária)\" style=\"color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">curado</a>&nbsp;produzido principalmente em&nbsp;<a href=\"https://pt.wikipedia.org/wiki/Espanha\" title=\"Espanha\" style=\"color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Espanha</a>&nbsp;e&nbsp;<a href=\"https://pt.wikipedia.org/wiki/Portugal\" title=\"Portugal\" style=\"color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Portugal</a>, baseado no&nbsp;<a href=\"https://pt.wikipedia.org/wiki/Porco_preto_ib%C3%A9rico\" title=\"Porco preto ibérico\" style=\"color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">porco preto ibérico</a>&nbsp;que também se designa como&nbsp;<i>porco de pata negra</i>&nbsp;ou&nbsp;<i>porco de raça alentejana</i>.&nbsp;<span style=\"line-height: 22.4px;\">Para a sua produção o animal deve contar com um mínimo de 50% de pureza desta raça para poder chamar-se \"presunto ibérico\".</span><span style=\"line-height: 22.4px;\">&nbsp;</span><span style=\"line-height: 22.4px;\">As principais características que distinguem os presuntos ibéricos pela qualidade provêm da pureza da raça dos animais, da criação em regime extensivo de liberdade em</span><span style=\"line-height: 22.4px;\">&nbsp;</span><a href=\"https://pt.wikipedia.org/wiki/Montado\" title=\"Montado\" style=\"line-height: 22.4px; color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">montados</a><span style=\"line-height: 22.4px;\">&nbsp;</span><span style=\"line-height: 22.4px;\">arborizados, onde os animais se movem livremente, e o processo de cura, que se estende dos 8 aos 36 meses, sendo tanto mais demorado quanto maior a peça e a quantidade de</span><span style=\"line-height: 22.4px;\">&nbsp;</span><a href=\"https://pt.wikipedia.org/wiki/Bolota\" title=\"Bolota\" style=\"line-height: 22.4px; color: rgb(11, 0, 128); background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">bolotas</a><span style=\"line-height: 22.4px;\">&nbsp;</span><span style=\"line-height: 22.4px;\">que o porco ingeriu.</span></p>', '', '', '', '', null, '', '301', '458.57', '655.79', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('671', '1', '1', '671', 'Whisky Johnnie Walker Red Label 8 Anos (750ml) Edição Limitada', '', 'Johnnie Walker Red Label É produzido com os melhores ingredientes e a tradição da família Johnnie Walker. Tem uma seleção inigualável de maltes na sua composição que garantem sua superioridade. São mais de 35 maltes, tendo como principal o Cardhu, reconhecido maltes das Terras Altas (Highlands). Talisker, outro importante malte na sua composição, vai garantir o paladar mais profundo e picante, oposto de Cardhu.', '', '', '', '', null, '', '101', '82.50', '117.99', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
