INSERT INTO `products` VALUES ('682', '1', '1', '682', 'Marreco - Marca: Vila Germany - (2kg)', '', 'O marreco é uma ave de carne leve e saborosa que serve para assar e cozinhar. É vendida limpa e com miúdos, fresco ou congelado. Quanto mais jovem a ave, mais tenra e suculenta a sua carne. Embora mais escura, é mais magra do que a do pato, mas igualmente saborosa. O peito tem mais gordura, porém concentrada entre músculo e pele, o que facilita a remoção.', '', '', '', '', null, '', '10', '50.16', '71.70', null, '2600', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
