INSERT INTO `products` VALUES ('692', '1', '1', '692', 'File Mignon Javali (700 grs) Congelado', '', '<span style=\"color: rgb(85, 85, 85); font-family: Arial, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(248, 251, 244);\">a carne de javali é uma carne extremamente saudável, é livre de hormônios, e completamente natural. Sua performance nutricional é a mais qualificada, ganhando inclusive de outras carnes consideradas exóticas.&nbsp;</span><span style=\"color: rgb(85, 85, 85); font-family: Arial, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(248, 251, 244);\">Ao contrário do que se pensa, a carne do javali é bem diferente da carne de porco comum. Para começar, ela&nbsp;retrai muito pouco ao ser cozida, tem o sabor peculiar &nbsp;da carne de caça, é natural, menos gordurosa e é muito mais saudável.</span><span style=\"color: rgb(85, 85, 85); font-family: Arial, sans-serif; font-size: 12px; line-height: 18px; background-color: rgb(248, 251, 244);\">Esta carne tem baixo teor de colesterol, gordura, e muito menos calorias do que a carne de porco, mesmo as que são consideradas “magras”.&nbsp;</span>', '', '', '', '', null, '', '190', '88.00', '125.80', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('693', '1', '1', '693', 'Linguiça de Picanha', '', 'Linguiça de Picanha Linguiça Artesanal de fabricação própria feita de picanha bovina moída em pedaços maiores, temperada com o tradicional tempero calabrês e pimenta dedo de moca fresca. Amarração individual (gomos de aproximadamente de 180 gramas).', '', '', '', '', null, '', '0', '33.98', '48.59', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
