INSERT INTO `products` VALUES ('734', '1', '1', '734', 'Jiló', '', 'O jilóé um dos frutos mais amargos que conhecemos, mas, além de ter essa característica forte, ele apresenta muitos benefícios que a maioria das pessoas desconhecem.É muito recomendado para quem gosta de manter a forma e fazer dietas, pois possui baixo valor calórico e seu gosto amargo é um excelente remédio para nosso sistema digestivo.', '', '', '', '', null, '', '196', '11.41', '16.30', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
