INSERT INTO `products` VALUES ('737', '1', '1', '737', 'Azeite de Oliva Extra Virgem Carbonell (500 ml)', '', 'A grande diferenciação e os benefícios de saudabilidade associados ao consumo do azeite de oliva estão diretamente relacionados à sua composição que é rica em ácidos graxos monoinsaturados, como o ácido oleico, e baixo em saturados o que favorece o controle do colesterol, pois pode ajudar a reduzir o colesterol ruim (LDL) no sangue, mantendo o nível de colesterol bom (HDL). Desse modo permitindo um equilíbrio saudável entre os dois tipos de colesterol. A presença de hidrocarbonetos no azeite favorece a excreção de toxinas, ajudando na saúde celular.', '', '', '', '', null, '', '78', '22.00', '31.49', null, '900', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('738', '1', '1', '738', 'Aceto Balsâmico LA PASTINA (500 ml)', '', 'Envelhecido em barris de \r\ncarvalho, castanheira, cerejeira, frassino e amoreira, que vão conferir \r\ncor, aroma, textura e paladar agridoce, é colocado posteriormente em \r\ngarrafões de cerâmica com gargalo protegida apenas por um pano de linho \r\nbranco, até sua maturação, no mínimo por três anos.', '', '', '', '', null, '', '99', '19.95', '28.50', null, '500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
