INSERT INTO `products` VALUES ('751', '1', '1', '751', 'Vinho Chileno Maipo Gran Devocion Cabernet/Syrah Safra 2009 (750 ml)', '', 'Origem: Chile.Descrição: Premiação: 90 WS e Medalha de Ouro Concurso Mundial de Bruxelas. Vinho tinto com nuances púrpura,apresenta aromas de fruta vermelha mesclada a notas de chocolate, tabaco e pimenta. Na boca é estruturado, equilibrado com taninos firmes e redondos com acidez marcante e um longo e persistente final. Uvas: Cabernet Sauvgnon e Syrah.  Harmonização: Ideal com massas com molho vermelho estruturado e bem temperado, carnes vermelhas e caças.', '', '', '', '', null, '', '0', '176.00', '251.69', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
