INSERT INTO `products` VALUES ('754', '1', '1', '754', 'Cachaça Beata Prata (600ml)', '', 'A Cachaça Beata Prata diferencia-se das demais por ser pura, isenta de \r\nenvelhecimento em tonéis de madeira branca, passando apenas por um \r\nprocesso de descanso de seis meses em dornas de inox. Deliciosa se \r\nservida pura, é ainda perfeita na confecção de drinks, principalmente na\r\n nossa tradicional caipirinha. Possui mosto fermentado do caldo de \r\ncana-de-açúcar. É inconfundível e mantém a tradição mineira, sendo elaborada\r\n de forma totalmente artesanal.', '', '', '', '', null, '', '11', '38.50', '55.09', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
