INSERT INTO `products` VALUES ('772', '1', '1', '772', 'Curry 500 grs', '', 'Curry é uma mistura de especiarias muito utilizada na culinária de países como Índia, Tailândia e alguns outros países asiáticos. É também um preparado típico da culinária indo-portuguesa de Goa, Damão e Diu, outrora pertencentes ao Estado Português da Índia, e da culinária de Moçambique, por influência da numerosa comunidade de origem goesa daquele país.', '', '', '', '', null, '', '30', '54.80', '78.39', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
