INSERT INTO `products` VALUES ('787', '1', '1', '787', 'Água de Flor de Laranjeira MECHALANE (grf 260 ml)', '', 'Na culinária, as mais conhecidas são a água de flor de laranjeira - essência extraída das pétalas da flor de laranjeira (mazahr em árabe), e a água de rosas - solução alcoólica de essência de rosas, muito diluída com água destilada (maward), ambas utilizadas para aromatizar sorvetes, massas, cremes, caldas e pratos à base de carne de aves, entre outras iguarias, sobretudo na cozinha árabe, libanesa e indiana.', '', '', '', '', null, '', '0', '13.47', '19.29', null, '500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('788', '1', '1', '788', 'Whisky Buchanans 18 Anos (750ml)', '', 'BuchanansFoi criado há mais de 1 século por James Buchanan. Em 1879, vivendo em Londres, começou a buscar por uma mistura que tivesse tanto qualidade quanto um sabor inconfundível. Em 1884, ele tinha criado um blend que ia ao encontro de seu desejo e como garantia da excepcional qualidade desse produto, colocou seu próprio nome nele. Sabor Malte.', '', '', '', '', null, '', '4', '825.00', '1179.79', null, '950', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
