INSERT INTO `products` VALUES ('809', '1', '1', '809', 'Cravo da Índia', '', '<p><span style=\"color: rgb(68, 68, 68); font-family: arial, helvetica, freesans, sans-serif; font-size: 16px; line-height: 24px; background-color: rgb(255, 255, 255);\">O cravo-da-índia (ou simplesmente “cravo”), é rico em óleos essenciais com ação analgésica e anti-séptica. Por apresentar flavonoides, o cravo-da-índia ajuda também a reduzir o nível de açúcar no sangue e pode ser utilizado como anti-inflamatório ou antibiótico. Quanto aos seu valor nutricional, também é fantástico, contém manganês, vitamina K, magnésio, cálcio, potássio e ácidos graxos ômega-3</span><span style=\"font-size: 14px;\">﻿</span></p>', '', '', '', '', null, '', '150', '90.00', '128.70', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('810', '1', '1', '810', 'Vinho Argentino Branco Chardonnay CATENA (750 ml', '', 'Catena Chardonnay é \r\numa das grandes referência para brancos maturados em barricas de \r\ncarvalho produzidos na América do Sul, mostrando muita concentração e \r\nfinesse. Sempre recebe ótimas notas da imprensa especializada, tendo \r\nsido chamado de \"delicioso\" pela revista Wine Spectator.O resultado é um\r\n excelente branco com um bouquet cativante de frutas brancas e nota \r\nmineral. Na boca é macio e pleno, com frutas maduras, mel, baunilha e um\r\n agradável e sutil toque tostado.', '', '', '', '', null, '', '12', '132.00', '188.70', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('811', '1', '1', '811', 'Vodka Belvedere Pink IX', '', 'Constantemente conectada a \r\nsofisticação e joie de vivre, Belvedere, marca do grupo de luxo LVMH, é \r\nelaborada com águas cristalinas de poços artesianos próprios e centeio \r\nda cidade de Dankowskie, na Polônia, seguindo uma receita artesanal \r\ntradicional de mais de 600 anos. Duplamente filtrada em carvão, \r\nBelvedere Pure oferece o equilíbrio perfeito entre pureza, personalidade\r\n e tradição, notas de baunilha e textura evidentemente superior aos seus\r\n principais concorrentes. Teor Alcoólico: 40%.', '', '', '', '', null, '', '12', '174.23', '249.19', null, '1400', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
