INSERT INTO `products` VALUES ('816', '1', '1', '816', 'Costela Bovina (kilo)', '', 'Costela Bovina também conhecida como pandorga e assado. Cercada de osso e gordura, é apropriada tanto para cozidos como para assados. É fibrosa e requer preparo lento, que pode chegar a 12 horas no \"bafo\" da churrasqueira, ou seja, longe da brasa.', '', '', '', '', null, '', '96', '20.75', '29.69', null, '2100', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('817', '1', '1', '817', 'Ancho Argentino (kg)', '', 'ANCHO é  um bifão de 5 cm de altura.Aspecto que deve ficar quando feito na grelha:  grelhado por fora, rosado por dentro. O corte, que já entrou para a galeria dos grandes legados argentinos à gastronomia, e esta virando a preferência brasileira. “Quem come uma vez repete sempre”.', '', '', '', '', null, '', '100', '47.08', '67.30', null, '1000', '12', '12', '2', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
