INSERT INTO `products` VALUES ('833', '1', '1', '833', 'Vinho Terrazas de Los Andes Afincado Malbec 2003 (750 ml', '', 'Característica: Vermelho escuro intenso, com reflexos violáceos. Aroma \r\nque predominam notas de frutas negras cristalizadas, no entanto, sua \r\nparticularidade são os toques de ameixas secas e uvas-passas. Ao agitar a\r\n taça, esses aromas persistem e surgem notas de alcaçuz, baunilha e \r\ncaramelo, tostado e defumado, que lhe dão grande complexidade. Sabor de \r\ncorpo e estrutura importantes, destaca-se pela presença da fruta na boca\r\n e pela untuosidade. Os toques de ameixas e passas conjugam-se com os de\r\n cerejas pretas e vermelhas. Seus taninos presentes, mas doces, dão-lhe \r\nvolume e persistência. É um vinho longo cujo sabor permanece na boca por\r\n vários segundos.', '', '', '', '', null, '', '12', '407.00', '582.00', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
