INSERT INTO `products` VALUES ('849', '1', '1', '849', 'Goiaba Desidratada', '', 'Goiaba desidratada em pedaços.Além de bastante nutritivas, as frutas desidratadas são gostosas e fazem muito bem a sua saúde, uma vez que elas não tem mais o líquído e todos os nutrientes estão concentrados em uma fina parte de fruta rica em fibras e carboidratos, mesmo tendo alguns nutrientes perdidos pelo fato da água ser removida da fruta.Uma boa forma de consumir essas frutas é logo na hora que dá vontade de comer algum tipo de doce.', '', '', '', '', null, '', '295', '87.89', '125.69', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
