INSERT INTO `products` VALUES ('875', '1', '1', '875', 'Azeite CRETAN PRINCE (5 lt)', '', 'Origem: Portugal É um produto que combina os atributos nutricionais de azeite extra virgem com um sabor frutuoso e aromático sutil e uma cor ouro-verde clara, característica do óleo de azeitona de Creta. Livre de qualquer substância química Cretan Prince é o suco natural da fruta de azeitona, livre de colesterol , rico em vitaminas E e A. Tipos de Azeitonas: Koroneiki e Tsounati. Acidez Máxima: 0,8%', '', '', '', '', null, '', '0', '79.19', '113.20', null, '10000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
