INSERT INTO `products` VALUES ('890', '1', '1', '890', 'Vinho Argentino San Felipe Roble Cabernet Sauvignon 2009 (750 ml)', '', 'Origem: Argentina.Descrição: Esse é um Cabernet Sauvignon 100%, oriundo a região de Maipú, na Argentina. Seu mosto é extraído a baixas temperaturas, o que garante a forte presença de fruta em seus aromas. Passa pela fermentação malolática em barricas de carvalho americano novas, nas quais estagia por 09 meses. O resultado de todo esse cuidado é um vinho extremamente perfumado, lembrando geléia de framboesa, notas de rosas, coco queimado e baunilha. Na boca, apresenta-se muito macio, com taninos doces e acidez bem discreta e equilibrada. Harmonização: massas, peixes e carnes vermelhas.', '', '', '', '', null, '', '26', '41.80', '59.79', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('891', '1', '1', '891', 'Vinho Argentino Alamos Malbec Tinto 2014 (750 ml)', '', 'Vinho Argentino,  é\r\n expressivo,  fresco, com adorável amplitude, textura sedosa e um final \r\nde boca  estruturado e cheio de taninos.Produtor: Alamos (Catena \r\nZapata) Região: Mendoza Uva: Malbec  Vinhedos: Vinhedos de \r\naltitudes elevadas em La Pirámide, Domingo, e Altamira todos localizados\r\n em Mendoza. Vinificação: Maceração de 23 dias. Fermentação \r\nalcoólica de 12 dias com leveduras selecionadas e temperaturas \r\ncontroladas.', '', '', '', '', null, '', '12', '77.00', '110.10', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
