INSERT INTO `products` VALUES ('915', '1', '1', '915', 'Licor Amarula (750ml)', '', 'Amarula é um licor  africano preparado com creme de leite e\r\n suco do frot da árvore africana maruleira   (Sclerocarya birrea),\r\n chega-se a um dos mais sofisticados licores Premium, um produto de \r\ntextura cremosa, estável, de paladar macio e saboroso.A\r\n Amarula possui um sabor suave semelhante ao caramelo. Pela associação da \r\nmaruleiro elefante, o fabricante fez do elefante o símbolo da marca \r\ncomercial e apoia um programa de conservação destes animais.', '', '', '', '', null, '', '8', '93.50', '133.70', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
