INSERT INTO `products` VALUES ('931', '1', '1', '931', 'Presunto Crú Serrano Reserva ( C/ Osso 24 meses cura)', '', 'Presunto Crú Serrano Reserva com Osso , muito saboroso e com ótima qualidade. Ideal para servir em qualquer ocasião.\r\nO peso pode variar em 20% +ou- peso 7,5 kilos em média.', '', '', '', '', null, '', '12', '714.38', '1021.59', null, '6500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
