INSERT INTO `products` VALUES ('959', '1', '1', '959', 'Aveia em Flocos Finos', '', 'A farinha de aveia é um alimento recomendado para crianças por ser rico em fibras, proteínas, vitaminas e sais minerais. 100% natural, é um alimento saudável, de fácil digestão, sendo acima de tudo gostoso. É indicada, também, na elaboração de pratos que requeiram maior consistência.', '', '', '', '', null, '', '199', '12.74', '18.20', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
