INSERT INTO `products` VALUES ('1043', '1', '1', '1043', 'Vinho Argentino Alamos Malbec Tinto (750ml)', '', 'Sem dúvida um dos \r\nmelhores achados em vinhos de extraordinária relação   qualidade/preço, o\r\n Alamos Malbec é uma verdadeira unanimidade. Ele tem   \"um estilo \r\nelegante e sofisticado, bem francês\" para Jancis   Robinson, é sempre um\r\n \"Best Value\" para a Wine Spectator e é   considerado um \"excellent \r\nvalue\" por Parker, que o descreve como   \"expressivo, fresco, com \r\nadorável amplitude, textura sedosa e um final   de boca estruturado e \r\ncheio de taninos\".', '', '', '', '', null, '', '10', '77.00', '110.10', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1044', '1', '1', '1044', 'Vinho Argentino ANGELICA ZAPATA Merlot Tinto 2011 (750ml)', '', 'Complexo e sofisticado, o cativante Angelica Zapata \r\nMerlot é um pouco mais macio e sedoso que cultuado Angelica Malbec, \r\nsendo uma companhia perfeita para massas, pizzas e até hamburgeres. Um \r\nvinho de pequena produção, antes vendido somente na Argentina, é uma \r\nverdadeira revelação para quem ainda não provou um grande Merlot deste \r\npaís.', '', '', '', '', null, '', '12', '203.50', '291.00', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1045', '1', '1', '1045', 'Vinho Pêra Manca Branco 2011 (750ml)', '', 'Origem: Português Região Alentejo Produtor Fundação Eugénio de Almeida Tipo Branco \r\nComposição Antão Vaz, Arinto Teor alcoólico 13,50% Visual Citrina Aroma \r\nMuito bom aroma com notas minerais, frutado, persistente, fino e \r\ncomplexo. Paladar Macio, seco com um bom corpo e um ligeiro acídulo \r\nnobre que lhe confere harmonia. Harmonização Bacalhau', '', '', '', '', null, '', '12', '462.00', '660.69', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
