INSERT INTO `products` VALUES ('1047', '1', '1', '1047', 'Queijo Maasdam (1kg)', '', 'Queijo MaasdamÉ caracterizado pela presença de grandes buracos, ideal para \r\nsanduíches e aperitivos, bem como na culinária, podendo ser usado no \r\npreparo de \"fondue\", gratinado e outros.', '', '', '', '', null, '', '27', '71.50', '102.20', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1048', '1', '1', '1048', 'Vinho Latitud 33° Malbec (750ml)', '', 'Origem: Argentina.Descrição: Vinho tinto argentino elaborado a partir de uvas viníferas da variedade Malbec. É um vinho jovem, pleno, de aromas frutados.Harmonizam com saladas, peixes, mariscos e carnes.', '', '', '', '', null, '', '23', '49.50', '70.79', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1049', '1', '1', '1049', 'Vinho Argentino Cobos BRAMARE Malbec Tinto (750ml)', '', 'Vinho de cor vermelho rubi com \r\nreflexo violeta denunciando sua juventude. No olfato é uma explosão de \r\nfrutas frescas com cereja, framboesa sobre um fundo de licor de cassis. \r\nNa boca repetiu a profusão de fruta dos aromas, com taninos doces e \r\nmuito finos, ótima acidez, bom entrosamento entre fruta (figo maduro) e \r\nmadeira.', '', '', '', '', null, '', '12', '341.00', '487.60', null, '1500', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
