INSERT INTO `products` VALUES ('1071', '1', '1', '1071', 'Vinho J. Lohr Estates Los Osos Merlot (750ml)', '', 'Apresenta cor rubi, com aroma de frutas vermelhas como amoras e cerejas, e notas de especiarias, baunilha e café. No paladar, uma estrutura cremosa, com taninos aveludados e perfeitamente integrados ao conjunto de acidez e álcool. De final longo e persistente.', '', '', '', '', null, '', '70', '198.00', '283.10', null, '1100', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
