INSERT INTO `products` VALUES ('1079', '1', '1', '1079', 'Vinho Mavrodaphne of Patras (750ml)', '', 'Este intrigante tinto doce de sobremesa é uma especialidade do norte do Peloponeso. Ele é elaborado com a uva Mavrodaphne, envelhecido em barricas por seis anos. Um vinho impressionante, com um delicioso e limpo frescor de boa concentração. Perfeito para combinação com chocolates.  Temos grande experiência na entrega do Vinho Mavrodaphne of Patras  e um processo rigoroso de embalagens garantindo a qualidade do produto e chegada em perfeito estado. Entrega em todo o Brasil.', '', '', '', '', null, '', '99', '214.50', '306.70', null, '1100', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
